import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Card, CardContent, Typography, Button, Grid } from '@mui/material';

const PostList = ({ onEdit, onDelete }) => {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/posts')
      .then((response) => {
        setPosts(response.data);
      })
      .catch((error) => {
        console.error('There was an error fetching the posts!', error);
      });
  }, []);

  return (
    <Grid container spacing={4}>
      {posts.map((post) => (
        <Grid item xs={12} sm={6} md={4} key={post._id}>
          <Card>
            <CardContent>
              <Typography variant="h5" component="div">{post.title}</Typography>
              <Typography variant="body2" color="text.secondary">{post.content}</Typography>
              <Button variant="contained" color="primary" onClick={() => onEdit(post)}>Edit</Button>
              <Button variant="contained" color="secondary" onClick={() => onDelete(post._id)}>Delete</Button>
            </CardContent>
          </Card>
        </Grid>
      ))}
    </Grid>
  );
};

export default PostList;
